import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VehicleList = () => {
  const [vehicles, setVehicles] = useState([]);
  const categories = ['sedan', 'suv', 'truck', 'sports'];

  useEffect(() => {
    axios.get('http://localhost:5000/api/vehicles')
      .then(res => setVehicles(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Vehicle Listings</h1>
      {categories.map(category => (
        <div key={category} className="mb-8">
          <h2 className="text-2xl font-semibold capitalize mb-4">{category}</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {vehicles
              .filter(v => v.category === category && v.status === 'available')
              .map(vehicle => (
                <div key={vehicle._id} className="bg-white p-4 rounded-lg shadow-md">
// ... (rest of the code remains the same)
<img src={`http://localhost:5000${vehicle.imageUrl}`} alt={vehicle.name} className="w-full h-48 object-cover rounded-md mb-2" />
// ...                  <h3 className="text-xl font-bold">{vehicle.name}</h3>
                  <p className="text-gray-600">${vehicle.price}</p>
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default VehicleList;
