import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SoldVehicles = () => {
  const [soldVehicles, setSoldVehicles] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/vehicles', {
      headers: { 'x-auth-token': localStorage.getItem('token') }
    })
      .then(res => setSoldVehicles(res.data.filter(v => v.status === 'sold')))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-12">
      <div className="container mx-auto px-4">
        <h1 className="text-4xl font-bold text-gray-800 mb-8 text-center">Sold Vehicles</h1>
        {soldVehicles.length === 0 ? (
          <p className="text-gray-500 text-center">No vehicles have been sold yet.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {soldVehicles.map(vehicle => (
              <div
                key={vehicle._id}
                className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition duration-300"
              >
                <img
                  src={`http://localhost:5000${vehicle.imageUrl}`}
                  alt={vehicle.name}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-xl font-bold text-gray-800 mb-2">{vehicle.name}</h3>
                  <p className="text-gray-600 mb-2">Sold on: {new Date(vehicle.soldDate).toLocaleDateString()}</p>
                  <p className="text-gray-500 text-sm">{vehicle.description}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SoldVehicles;
