import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // For redirect after logout

const UserPanel = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // For navigation

  const fetchUserInfo = async () => {
    const token = localStorage.getItem('token');
    console.log('Token from localStorage:', token); // Log token
    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:5000/api/auth/me', {
        headers: { 'x-auth-token': token },
      });
      console.log('User info fetched:', response.data);
      setUser(response.data);
    } catch (err) {
      console.error('Fetch error:', err.response?.data || err.message);
      setError(err.response ? err.response.data.message : 'Failed to fetch user info');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear token
    setUser(null); // Reset user state
    navigate('/login'); // Redirect to login page (adjust path as needed)
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-r from-gray-100 to-gray-200 flex flex-col items-center justify-center">
      <h1 className="text-4xl font-bold text-gray-800 mb-6">User Panel</h1>
      {error && <p className="text-red-500">{error}</p>}
      {user ? (
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <h2 className="text-2xl font-semibold mb-4">Welcome, {user.username}!</h2>
          <p className="text-gray-600">Email: {user.email}</p>
          <p className="text-gray-600">Role: {user.role}</p>
          <p className="text-gray-600">
            Joined: {new Date(user.createdAt).toLocaleDateString()}
          </p>
          <button
            onClick={handleLogout}
            className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Logout
          </button>
        </div>
      ) : (
        <p className="text-gray-600">Loading user info...</p>
      )}
    </div>
  );
};

export default UserPanel;
