import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SoldVehicles = () => {
  const [soldVehicles, setSoldVehicles] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/vehicles', {
      headers: { 'x-auth-token': localStorage.getItem('token') }
    })
      .then(res => setSoldVehicles(res.data.filter(v => v.status === 'sold')))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Sold Vehicles</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {soldVehicles.map(vehicle => (
          <div key={vehicle._id} className="bg-white p-4 rounded-lg shadow-md">
            <img src={`http://localhost:5000${vehicle.imageUrl}`} alt={vehicle.name} className="w-full h-48 object-cover rounded-md mb-2" />
            <h3 className="text-xl font-bold">{vehicle.name}</h3>
            <p className="text-gray-600">Sold on: {new Date(vehicle.soldDate).toLocaleDateString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SoldVehicles;
