import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VehicleList = () => {
  const [vehicles, setVehicles] = useState([]);
  const categories = ['sedan', 'suv', 'truck', 'sports'];

  useEffect(() => {
    axios.get('http://localhost:5000/api/vehicles')
      .then(res => setVehicles(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-12">
      <div className="container mx-auto px-4">
        <h1 className="text-4xl font-bold text-gray-800 mb-8 text-center">Vehicle Listings</h1>
        {categories.map(category => (
          <div key={category} className="mb-12">
            <h2 className="text-2xl font-semibold text-gray-700 capitalize mb-6">{category}</h2>
            {vehicles.filter(v => v.category === category && v.status === 'available').length === 0 ? (
              <p className="text-gray-500 text-center">No {category} vehicles available.</p>
            ) : (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {vehicles
                  .filter(v => v.category === category && v.status === 'available')
                  .map(vehicle => (
                    <div
                      key={vehicle._id}
                      className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition duration-300"
                    >
                      <img
                        src={`http://localhost:5000${vehicle.imageUrl}`}
                        alt={vehicle.name}
                        className="w-full h-48 object-cover"
                      />
                      <div className="p-4">
                        <h3 className="text-xl font-bold text-gray-800 mb-2">{vehicle.name}</h3>
                        <p className="text-gray-600 mb-2">${vehicle.price.toLocaleString()}</p>
                        <p className="text-gray-500 text-sm">{vehicle.description}</p>
                      </div>
                    </div>
                  ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default VehicleList;
